$(document).ready(function() {
// Function unutk mengubah align text
$("#alignButton").click(function() {
    $(".box").toggleClass("center-align");
    if ($(".box").hasClass("center-align")) {
        $("#alignButton").text("Align Right");
    } else {
        $("#alignButton").text("Align Center");
        $(".box").removeClass("right-align");
    }
});

// Function untuk mengubah warna
var originalColor = $(".box").css("background-color");
var newColor ="pink";
$("#colorButton").click(function() {
    if ($(".box").css("background-color") === originalColor) {
        $(".box").css("background-color", newColor);
        $("#colorButton").text("Restore Box Color");
    } else {
        $(".box").css("background-color", originalColor);
        $("#colorButton").text("Change Box Color");
    }
});
});